import React from 'react';
import { hero } from '../data';
import { HiOutlineChevronDown } from 'react-icons/hi';
import GlowingSnake from './GlowingSnake';

const Hero = () => {
  // Destructure hero data
  const { title, subtitle, btnText, compText, image } = hero;

  return (
    <section className='relative min-h-[900px] flex items-center justify-center text-center lg:text-left overflow-hidden'>

      {/* Background Video */}
      <video
        className="absolute top-0 left-0 w-full h-full object-cover z-[-1]"
        autoPlay
        loop
        muted
        playsInline
      >
        <source src="/videos/background.mp4" type="video/mp4" />
      </video>

      {/* Glowing Snake Animation */}
      <GlowingSnake />

      {/* Content Container */}
      <div className='container mx-auto min-h-[900px] flex justify-center items-center relative z-10'>
        <div className='flex flex-col md:flex-row gap-[30px] lg:gap-y-0 items-center justify-center'>

          {/* Text */}
          <div className='flex-1 max-w-xl'>
            <h1 className='text-[48px] md:text-[70px] font-bold text-white leading-tight mb-4 lg:mb-6' data-aos='fade-down' data-aos-delay='500'>
              {title}
            </h1>
            <p className='text-lg md:text-xl text-gray-300 leading-relaxed mb-6' data-aos='fade-down' data-aos-delay='600'>
              {subtitle}
            </p>

            {/* Buttons */}
            <div className='flex items-center gap-x-4' data-aos='fade-down' data-aos-delay='700'>
              <button className='btn btn-md lg:btn-lg btn-accent flex items-center gap-x-3'>
                {btnText}
                <HiOutlineChevronDown />
              </button>
              <p className='text-white text-lg'>{compText}</p>
            </div>
          </div>

          {/* Image */}
          <div className="w-full flex justify-center items-center p-4" data-aos="fade-up" data-aos-delay="800">
            <img 
              src={image} 
              alt="Hero" 
              className="w-[90%] sm:w-[70%] md:w-full max-w-[600px] h-auto block drop-shadow-lg"
            />
          </div>

        </div>
      </div>
    </section>
  );
};

export default Hero;
