import React from 'react';
import { hero } from '../data';
import { HiOutlineChevronDown } from 'react-icons/hi';
import GlowingSnake from './GlowingSnake'; // Import the snake component

const Hero = () => {
  // destructure hero data
  const { title, subtitle, btnText, compText, image } = hero;
  return (
    <section
      id="hero"
      className="relative bg-[#0A1931] min-h-[900px] py-12 flex items-center justify-center overflow-hidden"
    >
      <GlowingSnake /> {/* üêç Only inside Hero section */}

      <div className="container mx-auto flex flex-col lg:flex-row gap-[30px] gap-y-8 lg:gap-y-0 text-center lg:text-left relative z-10">
        {/* text */}
        <div className="flex-1">
          <h1 className="text-[60px] lg:text-[100px] font-bold text-white mb-5" data-aos="fade-down" data-aos-delay="500">
            {title}
          </h1>
          <p className="text-lg text-gray-300 mb-5 lg:mb-10" data-aos="fade-down" data-aos-delay="600">
            {subtitle}
          </p>

          <div className="flex items-center justify-center lg:justify-start gap-x-6" data-aos="fade-down" data-aos-delay="700">
            {/* button & comp text*/}
            <button className="btn btn-lg btn-accent flex items-center gap-x-4">
              {btnText}
              <HiOutlineChevronDown />
            </button>
            <p className="text-light text-lg">{compText}</p>
          </div>
        </div>

        {/* image */}
        <div className="flex-1 flex justify-center" data-aos="fade-up" data-aos-delay="800">
          <img src={image} alt="Hero" className="w-[90%] sm:w-[70%] md:w-full max-w-[600px] h-auto block" />
        </div>
      </div>
    </section>
  );
};

export default Hero;
