import React, { useEffect } from "react";
import { gsap } from "gsap";

const GlowingSnake = () => {
  useEffect(() => {
    // Animate the snake along the path
    gsap.to("#snake", {
      strokeDasharray: 800,
      strokeDashoffset: 800,
      duration: 10,
      repeat: -1,
      ease: "power1.inOut",
      strokeDashoffset: 0,
    });

    // Floating effect for the snake
    gsap.to("#snake", {
      y: 10,
      repeat: -1,
      yoyo: true,
      duration: 2,
      ease: "sine.inOut",
    });
  }, []);

  return (
    <div className="absolute inset-0 w-full h-full pointer-events-none">
      {/* Glowing Snake Animation */}
      <svg
        className="absolute w-full h-full"
        viewBox="0 0 1200 600"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          id="snake"
          d="M50,300 Q400,100 600,300 T1200,300"
          stroke="rgba(0, 100, 255, 0.8)" 
          strokeWidth="8"
          fill="none"
          strokeLinecap="round"
          strokeLinejoin="round"
          filter="url(#glow)"
        />
        {/* Glowing effect */}
        <defs>
          <filter id="glow">
            <feGaussianBlur stdDeviation="6" result="coloredBlur" />
            <feMerge>
              <feMergeNode in="coloredBlur" />
              <feMergeNode in="SourceGraphic" />
            </feMerge>
          </filter>
        </defs>
      </svg>
    </div>
  );
};

export default GlowingSnake;
