import React, { useEffect } from "react";
import { gsap } from "gsap";

const FloatingLadybugs = () => {
  useEffect(() => {
    // Animate each ladybug with random movements
    const ladybugs = document.querySelectorAll(".ladybug");
    ladybugs.forEach((bug, index) => {
      gsap.to(bug, {
        x: gsap.utils.random(-200, 200),
        y: gsap.utils.random(-100, 100),
        duration: gsap.utils.random(3, 6),
        repeat: -1,
        yoyo: true,
        ease: "sine.inOut",
        delay: index * 0.5,
      });
    });
  }, []);

  return (
    <div className="fixed inset-0 w-full h-full pointer-events-none z-[-1]">
      {/* Animated Ladybugs */}
      {[...Array(6)].map((_, index) => (
        <div
          key={index}
          className="ladybug absolute w-[40px] h-[40px]"
          style={{
            top: `${Math.random() * 100}vh`,
            left: `${Math.random() * 100}vw`,
          }}
        >
          <svg
            width="40"
            height="40"
            viewBox="0 0 100 100"
            xmlns="http://www.w3.org/2000/svg"
          >
            {/* Body */}
            <circle cx="50" cy="50" r="30" fill="red" stroke="black" strokeWidth="3" />

            {/* Head */}
            <circle cx="50" cy="20" r="12" fill="black" />

            {/* Line in middle */}
            <line x1="50" y1="20" x2="50" y2="80" stroke="black" strokeWidth="2" />

            {/* Spots */}
            <circle cx="35" cy="40" r="5" fill="black" />
            <circle cx="40" cy="60" r="5" fill="black" />
            <circle cx="60" cy="40" r="5" fill="black" />
            <circle cx="55" cy="60" r="5" fill="black" />

            {/* Legs */}
            <line x1="30" y1="50" x2="15" y2="45" stroke="black" strokeWidth="3" />
            <line x1="30" y1="60" x2="15" y2="65" stroke="black" strokeWidth="3" />
            <line x1="70" y1="50" x2="85" y2="45" stroke="black" strokeWidth="3" />
            <line x1="70" y1="60" x2="85" y2="65" stroke="black" strokeWidth="3" />
          </svg>
        </div>
      ))}
    </div>
  );
};

export default FloatingLadybugs;
